/* 
 * @staff [local]
 * Add a way for normal users to
 * ping admins and moderators in
 * chat using @mods and @admins
 *
*/
 
var settings = {
    version : '1.0.1',
    retries: 3
};
 
var userlevel;
 
var config = { attributes: true, childList: true, characterData: true, subtree: true };
 
var observer = new MutationObserver(function(mutations) {
    mutations.forEach(function(mutation) {
        if(mutation.type != "childList") return;
        for(var x = mutation.addedNodes.length - 1; x > -1; --x){
            if (mutation.addedNodes[x].nodeType != 1) continue;
            var node = mutation.addedNodes[x];
            if(node.hasAttribute("data-user")) {
                var username = node.getAttribute("data-user");
                var userelement = node.querySelector("span.username");
                var message = node.querySelector("span.message");
                //On messages, check contents
                methodEvent(message.textContent);
            } else if(node.classList.contains("inline-alert")) {
                var text = node.textContent.trim();
            }
        }
    });
});

function loadMO() {
    //Get the usergroups of the user
    var groups = mw.config.get("wgUserGroups").join(" ");
    if (groups.indexOf("sysop") > -1) {
        //If they are an admin
        userlevel = 2;
    } else if (groups.indedOf("chatmoderator") > -1) {
        //If they are a moderator
        userlevel = 1;
    } else {
        //Otherwise, don't do anything
        return;
    }
    if(settings.retries < 1) {
        console.log("Failed to load version " + settings.version);
        return;
    }
    target = document.querySelector('div.Chat ul');
    if( target === null) {
        settings.retries --;
        setTimeout(loadMO, 1000);
    } else {
        observer.observe(target, config);
        console.log("Successfully loaded version " + settings.version);
    }
 
}

loadMO(settings.retries);

function methodEvent(message) {
    if (message.substring(0, 5) == "@mods" && userlevel > 0) {
        beep();
        console.log("@mods pinged");
    } else if (message.substring(0,7) == "@admins" && userlevel == 2) {
        beep();
        console.log("@admins pinged");
    } else {
        return;
    }
}
 
 
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}