var str= "";
str += "<script type=\"text/javascript\">";
str += "// <![CDATA[";
str += "var colour=\"#FFFFFF\";";
str += "var sparkles=120;";
str += "";
str += "/****************************";
str += "*  Tinkerbell Magic Sparkle *";
str += "* (c) 2005 mf2fm web-design *";
str += "*  http://www.mf2fm.com/rv  *";
str += "* DON'T EDIT BELOW THIS BOX *";
str += "****************************/";
str += "var x=ox=400;";
str += "var y=oy=300;";
str += "var swide=800;";
str += "var shigh=600;";
str += "var sleft=sdown=0;";
str += "var tiny=new Array();";
str += "var star=new Array();";
str += "var starv=new Array();";
str += "var starx=new Array();";
str += "var stary=new Array();";
str += "var tinyx=new Array();";
str += "var tinyy=new Array();";
str += "var tinyv=new Array();";
str += "";
str += "window.onload=function() { if (document.getElementById) {";
str += "  var i, rats, rlef, rdow;";
str += "  for (var i=0; i<sparkles; i++) {";
str += "    var rats=createDiv(3, 3);";
str += "    rats.style.visibility=\"hidden\";";
str += "    document.body.appendChild(tiny[i]=rats);";
str += "    starv[i]=0;";
str += "    tinyv[i]=0;";
str += "    var rats=createDiv(5, 5);";
str += "    rats.style.backgroundColor=\"transparent\";";
str += "    rats.style.visibility=\"hidden\";";
str += "    var rlef=createDiv(1, 5);";
str += "    var rdow=createDiv(5, 1);";
str += "    rats.appendChild(rlef);";
str += "    rats.appendChild(rdow);";
str += "    rlef.style.top=\"2px\";";
str += "    rlef.style.left=\"0px\";";
str += "    rdow.style.top=\"0px\";";
str += "    rdow.style.left=\"2px\";";
str += "    document.body.appendChild(star[i]=rats);";
str += "  }";
str += "  set_width();";
str += "  sparkle();";
str += "}}";
str += "";
str += "function sparkle() {";
str += "  var c;";
str += "  if (x!=ox || y!=oy) {";
str += "    ox=x;";
str += "    oy=y;";
str += "    for (c=0; c<sparkles; c++) if (!starv[c]) {";
str += "      star[c].style.left=(starx[c]=x)+\"px\";";
str += "      star[c].style.top=(stary[c]=y)+\"px\";";
str += "      star[c].style.clip=\"rect(0px, 5px, 5px, 0px)\";";
str += "      star[c].style.visibility=\"visible\";";
str += "      starv[c]=50;";
str += "      break;";
str += "    }";
str += "  }";
str += "  for (c=0; c<sparkles; c++) {";
str += "    if (starv[c]) update_star(c);";
str += "    if (tinyv[c]) update_tiny(c);";
str += "  }";
str += "  setTimeout(\"sparkle()\", 40);";
str += "}";
str += "";
str += "function update_star(i) {";
str += "  if (--starv[i]==25) star[i].style.clip=\"rect(1px, 4px, 4px, 1px)\";";
str += "  if (starv[i]) {";
str += "    stary[i]+=1+Math.random()*3;";
str += "    if (stary[i]<shigh+sdown) {";
str += "      star[i].style.top=stary[i]+\"px\";";
str += "      starx[i]+=(i%5-2)/5;";
str += "      star[i].style.left=starx[i]+\"px\";";
str += "    }";
str += "    else {";
str += "      star[i].style.visibility=\"hidden\";";
str += "      starv[i]=0;";
str += "      return;";
str += "    }";
str += "  }";
str += "  else {";
str += "    tinyv[i]=50;";
str += "    tiny[i].style.top=(tinyy[i]=stary[i])+\"px\";";
str += "    tiny[i].style.left=(tinyx[i]=starx[i])+\"px\";";
str += "    tiny[i].style.width=\"2px\";";
str += "    tiny[i].style.height=\"2px\";";
str += "    star[i].style.visibility=\"hidden\";";
str += "    tiny[i].style.visibility=\"visible\"";
str += "  }";
str += "}";
str += "";
str += "function update_tiny(i) {";
str += "  if (--tinyv[i]==25) {";
str += "    tiny[i].style.width=\"1px\";";
str += "    tiny[i].style.height=\"1px\";";
str += "  }";
str += "  if (tinyv[i]) {";
str += "    tinyy[i]+=1+Math.random()*3;";
str += "    if (tinyy[i]<shigh+sdown) {";
str += "      tiny[i].style.top=tinyy[i]+\"px\";";
str += "      tinyx[i]+=(i%5-2)/5;";
str += "      tiny[i].style.left=tinyx[i]+\"px\";";
str += "    }";
str += "    else {";
str += "      tiny[i].style.visibility=\"hidden\";";
str += "      tinyv[i]=0;";
str += "      return;";
str += "    }";
str += "  }";
str += "  else tiny[i].style.visibility=\"hidden\";";
str += "}";
str += "";
str += "document.onmousemove=mouse;";
str += "function mouse(e) {";
str += "  set_scroll();";
str += "  y=(e)?e.pageY:event.y+sdown;";
str += "  x=(e)?e.pageX:event.x+sleft;";
str += "}";
str += "";
str += "function set_scroll() {";
str += "  if (typeof(self.pageYOffset)==\"number\") {";
str += "    sdown=self.pageYOffset;";
str += "    sleft=self.pageXOffset;";
str += "  }";
str += "  else if (document.body.scrollTop || document.body.scrollLeft) {";
str += "    sdown=document.body.scrollTop;";
str += "    sleft=document.body.scrollLeft;";
str += "  }";
str += "  else if (document.documentElement && (document.documentElement.scrollTop || document.documentElement.scrollLeft)) {";
str += "    sleft=document.documentElement.scrollLeft;";
str += " sdown=document.documentElement.scrollTop;";
str += "  }";
str += "  else {";
str += "    sdown=0;";
str += "    sleft=0;";
str += "  }";
str += "}";
str += "";
str += "window.onresize=set_width;";
str += "function set_width() {";
str += "  if (typeof(self.innerWidth)==\"number\") {";
str += "    swide=self.innerWidth;";
str += "    shigh=self.innerHeight;";
str += "  }";
str += "  else if (document.documentElement && document.documentElement.clientWidth) {";
str += "    swide=document.documentElement.clientWidth;";
str += "    shigh=document.documentElement.clientHeight;";
str += "  }";
str += "  else if (document.body.clientWidth) {";
str += "    swide=document.body.clientWidth;";
str += "    shigh=document.body.clientHeight;";
str += "  }";
str += "}";
str += "";
str += "function createDiv(height, width) {";
str += "  var div=document.createElement(\"div\");";
str += "  div.style.position=\"absolute\";";
str += "  div.style.height=height+\"px\";";
str += "  div.style.width=width+\"px\";";
str += "  div.style.overflow=\"hidden\";";
str += "  div.style.backgroundColor=colour;";
str += "  return (div);";
str += "}";
str += "// ]]>";
str += "<\/script>";
str += "";