$(function(){
	var _showTab = 0;
	$('.news').each(function(){
			var $tab = $(this);
	$('ul.tabNav li',$tab).eq(_showTab).addClass('active');
	$('.tabContent',$tab).hide().eq(_showTab).fadeIn();
	$('ul.tabNav li',$tab).mouseover(function() {
		var $this = $(this),
			_clickTab = $this.find('a').attr('href');
		$this.addClass('active').siblings('.active').removeClass('active');
		$(_clickTab).stop(false, true).fadeIn(200).siblings().hide();
 		return false;
	}).find('a').focus(function(){
		this.blur();
	});
	});
});