$(function() {
	if (wgCanonicalNamespace == 'Top_10_list') {   
		var replaceParagraph = $('#WikiaArticle #mw-content-text > p');
		var replaceParagraphContent = $('#WikiaArticle #mw-content-text > p').html();
		if(replaceParagraphContent == "Which vodka drink featured in the AMC series, Mad Men, do you want the Wikia Staff to make?") {
			var replaceText = '<a href="http://madmen.wikia.com/wiki/User_blog:Asnow89/Food_Fiction:_Mad_Men_Cocktail_Party"; title="Mad Men Cocktail Party"><img src="https://images.wikia.nocookie.net/__cb20130515200452/madmen/images/f/fb/FF_MadMen_BlogHeader_660x220.jpg" alt="Mad Men Cocktail Party" /></a>';
			replaceParagraph.html(replaceText);
		}
	}
});

