class Button {
	constructor(
				link='https://nopixel.fandom.com',
				backSize='300% 100%',
				fontSize='1em',
				width='100px',
				color='white',
				margin='',
				padding='',
				gradient='linear-gradient(to right, #25aae1, #40e495, #30dd8a, #2bb673)',
				boxShadow='0 4px 15px 0 rgba(49, 196, 190, 0.75)'
	){
		this.link = link;
		this.backSize = backSize;
		this.fontSize = fontSize;
		this.width = width;
		this.color = color;
		this.margin = margin;
		this.padding = padding;
		this.gradient = gradient;
		this.boxShadow = boxShadow;
	}
};