document.write("<LINK REL=STYLESHEET TYPE='text/css' HREF='http://www.miastogier.pl/mod/Encyklopedia/style/licznik.css'>");document.write("<center><div class='cntr arial14 kolorGrey2 rogi5px bd1BROWN' style='background: #303644 url(http://tanie--loty.net.pl/wp-content/uploads/2013/08/Plany-wakacyjne-Europejczykow-2012-Brogau.jpg) no-repeat left center; width: 280px; text-shadow: 0px 0px 3px #000, 0px 0px 3px #000, 0px 0px 3px #000, 0px 0px 5px #000, 0px 0px 5px #000; background-size: cover; padding: 5px 10px; font-weight: bold; opacity: 0.7;'>Początek wakacji za:<div id='countbox242140' class='countbox'></div></div></center>");dateFuture242140 = new Date(2015,06,27,0,0,0);
			function GetCount242140(ddate,iid){ dateNow = new Date(); amount = ddate.getTime() - dateNow.getTime(); delete dateNow; if(amount < 0){ document.getElementById(iid).innerHTML='Już są wakacje!'; } else { days=0;hours=0;mins=0;secs=0;out=''; amount = Math.floor(amount/1000); days=Math.floor(amount/86400); amount=amount%86400; hours=Math.floor(amount/3600); amount=amount%3600; mins=Math.floor(amount/60); amount=amount%60; secs=Math.floor(amount); if(days != 0){out += '<b>'+ days +''+((days==1)?'dzień':'dni')+'</b>';} if(hours != 0){out += '<b>'+ hours +''+((hours==1)?'h':'h')+'</b>';} out += '<b>'+ mins +''+((mins==1)?'min':'min')+'</b>'; out += '<b>'+ secs +''+((secs==1)?'s':'s')+'</b>'; out = out.substr(0,out.length-1); document.getElementById(iid).innerHTML=out; setTimeout(function(){GetCount242140(ddate,iid)}, 1000); } } GetCount242140(dateFuture242140, 'countbox242140');