function createCollapses() {
    var spoilerDivs = document.getElementsByTagName("div");
    var tempC = '';
    var j = 1;
    for (var i=0;i<spoilerDivs.length;i++) {
        if (hasClass(spoilerDivs[i],"spoiler")) {
             tempC = spoilerDivs[i].innerHTML;
             spoilerDivs[i].innerHTML = '<span class="clicker" title="Click to see the spoiler text" onclick="switchSpoiler(\'spoilerSpan-'+j+'\');">!Spoiler!</span><div id="spoilerSpan-'+j+'">'+ tempC +'</div>';
             spoilerDivs[i].style.display = 'block';
             j++;
            }
        }
    }
var switchSpoiler = (function() {
    return function(names) {
        elem = document.getElementById(names);
        if (elem.style.display == 'block') elem.style.display = 'none';
        else elem.style.display = 'block';
        };
})();
var hasClass = (function () {
    var reCache = {};
    return function (element, className) {
        return (reCache[className] ? reCache[className] : (reCache[className] = new RegExp("(?:\\s|^)" + className + "(?:\\s|$)"))).test(element.className);
    };
})();
addOnloadHook(createCollapses);
addOnloadHook(helpWithFeats);